#' Magma colors
#'
#' The \code{inferno} color palette, by Nathaniel J. Smith and Stefan van der Walt (CC0 license).
#'
#' @format A vector of length 256 containing hex values.
#' @source \url{https://bids.github.io/colormap/} for the concept and \url{https://github.com/BIDS/colormap/blob/master/colormaps.py} for the data.
#' @family mpl palettes
"inferno"


#' Magma color scale and palette
#'
#' The \code{inferno} color palette, by Nathaniel J. Smith and Stefan van der Walt (CC0 license).
#'
#' @inheritParams interp_scale
#'
#' @template return_scales
#'
#' @export
#'
#' @family color scales and palettes
#'
#' @seealso \code{\link{inferno}} for the colors in the palette.
#'
#' @examples
#' # Get a few colors along the palette
#' show_col(
#'   inferno_palette()(20),
#'   inferno_colors(50),
#'   inferno_colors(20, reverse=TRUE)
#' )
#'
#' # 1/ Represent a continuous variable
#'
#' # Map the elevation of the Maunga Whau volcano
#' image(maunga, col=inferno_colors(100), asp=1)
#' contour(maunga, col=alpha("white", 0.5), add=TRUE)
#'
#' persp(maunga, theta=50, phi=25, scale=FALSE, expand=2,
#'       border=alpha("black", 0.4),
#'       col=inferno_map(persp_facets(maunga$z)))
#'
#' \dontrun{
#' # with ggplot2
#' library("ggplot2")
#' ggplot(maungaxyz) + coord_fixed() +
#'   geom_raster(aes(x=x, y=y, fill=z)) +
#'   geom_contour(aes(x=x, y=y, z=z), color="white", alpha=0.5) +
#'   scale_fill_inferno()
#'
#' # in spinning 3D
#' library("rgl")
#' persp3d(maunga, aspect=c(1,0.7,0.2), axes=FALSE, box=FALSE,
#'         col=inferno_map(maunga$z))
#' play3d(spin3d(axis=c(0, 0, 1), rpm=10), duration=6)}
#'
#' # Represent a third variable on a scatterplot
#' attach(airquality)
#' # define a scale encompassing the whole data
#' my_scale <- inferno_scale(domain=c(0,200))
#' # use the same scale for the plot and the legend
#' pars <- sidemargin()
#' plot(Wind, Temp, col=my_scale(Ozone), pch=19)
#' sidelegend(legend=c(pretty(Ozone), "NA"),
#'            col=my_scale(c(pretty(Ozone), NA)), pch=19)
#' par(pars)
#'
#' \dontrun{
#' # or with ggplot2
#' # but the light yellows at the top of the scale are difficult to see
#' # on points; either outline them or put them on a dark background
#' ggplot(airquality) +
#'   geom_point(aes(x=Wind, y=Temp, fill=Ozone), shape=21, size=2) +
#'   scale_fill_inferno()
#' ggplot(airquality) + theme_dark() +
#'   geom_point(aes(x=Wind, y=Temp, color=Ozone)) +
#'   scale_color_inferno(na.value="grey60")}
#'
#'
#' # 2/ Represent a discrete variable
#' # albeit only with a limited number of levels
#'
#' attach(iris)
#' pars <- sidemargin()
#' plot(Petal.Length, Petal.Width, pch=21, bg=inferno_map(Species))
#' sidelegend(legend=levels(Species),
#'            pt.bg=inferno_colors(n=nlevels(Species)), pch=21)
#' par(pars)
#'
#' \dontrun{
#' # or with ggplot2
#' ggplot(iris) +
#'   geom_point(aes(Petal.Length, Petal.Width, fill=Species), shape=21) +
#'   scale_fill_inferno_d()}
inferno_scale <- function(domain=c(0,1), reverse=FALSE, na.value=NULL, extrapolate=FALSE) {
  # get everything into numbers
  domain <- as.num(domain)
  if (reverse) { domain <- rev(domain)}
  f <- function(x) {
    x <- as.num(x)
    # compute colors
    xs <- rescale(x, from=domain, to=c(0,1))
    colors <- scales::colour_ramp(chroma::inferno)(xs)
    return(post_process_scale(colors, inferno_na(na.value), extrapolate, x, domain))
  }
  return(f)
}

#' @param ... passed to \code{\link{inferno_scale}} from other \code{inferno_*} functions; passed to \code{ggplot2::\link[ggplot2]{continuous_scale}} or \code{ggplot2::\link[ggplot2]{discrete_scale}} from the \code{scale_*} functions, as appropriate. NB: in all situations, passing \code{domain} is meaningless and yields an error.
#' @rdname inferno_scale
#' @export
inferno_map <- function(x, ...) { as_map(inferno_scale, x,  ...) }

#' @rdname inferno_scale
#' @export
inferno_palette <- function(...) { as_palette(inferno_scale, ...) }

#' @param n number of colors to extract from the color palette.
#' @rdname inferno_scale
#' @export
inferno_colors <- function(n, ...) { inferno_palette(...)(n) }

# Pick and appropriate NA value for a inferno scale
inferno_na <- function(na.value) {
  if (is.null(na.value)) {
    na.value <- desaturate(average(inferno_colors(50)), 10)
    # = grey of luminance equal to the average color of the scale
  }
  return(na.value)
}


## ggplot2 ----

#' @rdname inferno_scale
#' @export
scale_color_inferno_c <- function(..., reverse=FALSE, na.value=NULL, guide="colorbar") {
  cols <- if(reverse) rev(chroma::inferno) else chroma::inferno
  ggplot2::continuous_scale("colour", "inferno",
    scales::colour_ramp(cols),
    na.value=inferno_na(na.value), guide=guide, ...
  )
}
#' @rdname inferno_scale
#' @export
#' @usage NULL
scale_colour_inferno_c <- scale_color_inferno_c

#' @rdname inferno_scale
#' @export
scale_fill_inferno_c <- function(..., reverse=FALSE, na.value="#818181", guide="colorbar") {
  cols <- if(reverse) rev(chroma::inferno) else chroma::inferno
  ggplot2::continuous_scale("fill", "inferno",
    scales::colour_ramp(cols),
    na.value=inferno_na(na.value), guide=guide, ...
  )
}

#' @rdname inferno_scale
#' @export
scale_color_inferno_d <- function(..., reverse=FALSE, na.value="#818181", guide="legend") {
  cols <- if(reverse) rev(chroma::inferno) else chroma::inferno
  ggplot2::discrete_scale("colour", "inferno",
    function(n) {scales::colour_ramp(cols)(seq(0,1,length.out=n))},
    na.value=na.value, ...
  )
}
#' @rdname inferno_scale
#' @export
#' @usage NULL
scale_colour_inferno_d <- scale_color_inferno_d

#' @rdname inferno_scale
#' @export
scale_fill_inferno_d <- function(..., reverse=FALSE, na.value="#818181", guide="legend") {
  cols <- if(reverse) rev(chroma::inferno) else chroma::inferno
  ggplot2::discrete_scale("fill", "inferno",
    function(n) {scales::colour_ramp(cols)(seq(0,1,length.out=n))},
    na.value=na.value, ...
  )
}

# Make the continuous versions the default because it is the most common use case
#' @rdname inferno_scale
#' @export
#' @usage NULL
scale_fill_inferno <- scale_fill_inferno_c
#' @rdname inferno_scale
#' @export
#' @usage NULL
scale_color_inferno <- scale_color_inferno_c
#' @rdname inferno_scale
#' @export
#' @usage NULL
scale_colour_inferno <- scale_color_inferno_c


